      class ListComponent extends React.Component {
        constructor(props){
          super(props);
          this.state = {
            status: 'local', // local, remote, connectedTo 
            nodes: [],
            text: '',
            pastable: false,
            pasteText: ''
          }
        }

        componentDidMount(){
          this.fetchAll();


          universe.EE.on('nodes-modified', this.fetchAll);

        }
        componentWillUnmount(){
          universe.EE.removeListener('nodes-modified', this.fetchAll);
        }

        @autobind
        handleChangeStatus(status){
          this.setState({
            status
          }, this.fetchAll);
        }

        @autobind
        async fetchAll(){

          // Get local code nodes 
          let LocalCodeNodes; // = await this.fetchLocalNodes();

          // Get latest for local nodes from NodeChain
          switch(this.state.status){
            case 'local':
              LocalCodeNodes = await this.fetchLocalNodes();
              break;
            case 'remote':
              LocalCodeNodes = await this.fetchRemoteNodes();
              break;
            case 'thru':
              // TODO
            default:
              break;

          }

          await this.fetchUpdatedNodes(LocalCodeNodes);

        }

        @autobind
        fetchLocalNodes(){

          // 
          return new Promise(async (resolve,reject)=>{


            // Fetch all of my local Code/React nodes 
            // - get the key (unique) for each) 
            // - ask Teacher Second for code matches for code items (and react components) 
            let CodeNodes = await universe.searchMemory({
              filter: {
                filterNodes: tmpNodes=>{
                  return new Promise((resolve, reject)=>{
                    tmpNodes = tmpNodes.map(tmpNode=>{
                      let types = [
                        'code',
                        'react_component',
                        'preparse_code',
                        'action_code'
                      ]
                      if(types.indexOf(tmpNode.type.split(':')[0]) === -1){
                        return false;
                      }
                      delete tmpNode.parent;
                      delete tmpNode.nodes;
                      return tmpNode;
                    })
                    resolve(universe.lodash.compact(tmpNodes));
                  });
                },
              }
            });


            // let componentNode = await universe.runInVM(result[0],{});
            this.setState({
              nodes: CodeNodes
            })

            return resolve(CodeNodes);


          });

        }

        @autobind
        fetchRemoteNodes(){

          // 
          return new Promise(async (resolve,reject)=>{

            try {

              this.setState({
                fetching: true
              })

              let ExternalIdentityNodes = await universe.searchMemory({
                filter: {
                  sqlFilter: {
                    type: 'external_identity:0.0.1:local:8982f982j92',
                    data: {
                      commonName: 'main'
                    }
                  }
                }
              });
              let ExternalIdentityNode = ExternalIdentityNodes[0];


              this.props.setState({
                OwnerSecondExternalIdentityNode: ExternalIdentityNode
              });


              let response = await universe.loadAndRunCapability('TalkToSecond',{},{
                type: 'standard_capability_action:0.0.1:local:298j291bs',
                data: {
                  action: 'send',
                  options: {
                    ExternalIdentityNode: ExternalIdentityNode,
                    RequestNode: {
                      type: 'run_action_sequence:0.0.1:local:293fh8239hsdf23f',
                      data: {
                        actions: [
                          {
                            matchActionType: 'search_internal_datasource:0.0.1:local:2h3ufih8s9h2f',
                            dataForAction: {
                              type: 'standard_query_request:0.0.1:local:65723f2khfds',
                              data: {
                                matchFunctionNode: {
                                  // query here!
                                  // action_pointer:0.0.1:local:238972ncr
                                  type: 'code_miniverse:0.0.1:local:2782fh8823s23',
                                  data: {
                                    code: `(()=>{
                                      // Action 
                                      // - childNode common_type:0.0.1:local:298fh29h2323f == "action" 

                                      let inputNode = INPUT.data.inputNode;

                                      let types = [
                                        'code',
                                        'react_component',
                                        'preparse_code',
                                        'action_code'
                                      ]
                                      if(types.indexOf(inputNode.type.split(':')[0]) === -1){
                                        return false;
                                      }

                                      delete inputNode.parent;

                                      return inputNode;

                                    })()`
                                  }
                                },
                                // expected/allowed schemas for return 
                                // outputSchemas: [
                                //   'query_result:0.0.1:local:32490usfj23o23f',
                                //   'internal_error_output:0.0.1:local:32948x2u3cno2c'
                                // ]
                              }
                            }
                          }
                        ]

                      }
                    }
                  }
                }
              });

              // let nodes = universe.lodash.find(response.data.actionResponses[0].data[0].nodes,{type: 'code:0.0.1:local:32498h32f2'});
              let nodes = response.data.actionResponses[0].data;

              // let componentNode = await universe.runInVM(result[0],{});
              this.setState({
                nodes: nodes,
                fetching: false
              })

              this.fetchUpdatedNodes(nodes);

            } catch(err){
              console.error('Failed getting remote nodes');
              WINDOW.alert('Failed getting remote nodes');

              this.setState({
                fetching: false
              })

            }

          });

        }

        @autobind
        fetchUpdatedNodes(CodeNodes){
          return new Promise(async (resolve,reject)=>{

            // Search chains for latest nodes (must have author/ref)  

            this.setState({
              fetching: true
            })


            // searching ALL chains that have ANY matches 
            // - exposes to each API all my code nodes, which isn't ideal 
            //   - TODO: make more selective for querying; some code nodes just need to query themselves 


            let searches = [];
            let chainsToSearch = [];
            CodeNodes.forEach(node=>{
              if(!node.data.key){
                // skipping, no author!
                node.__status = 'missing_local_ref' // update node by reference
                return;
              }
              node.__status = 'searching';
              let obj = {};
              obj.ref = node.data.key;
              if(node.data.author){
                obj.author = node.data.author;
              }
              searches.push(obj);
              chainsToSearch.push(node.data.nodeChainNode);
            })

            this.setState({
              nodes: CodeNodes
            })

            // get unique chains 
            chainsToSearch = universe.lodash.uniqBy(chainsToSearch, (nodeChainNode)=>{
              if(!nodeChainNode){
                // skips 
                return false;
              }

              return [nodeChainNode.data.network, nodeChainNode.data.username].join('');

            });

            chainsToSearch = universe.lodash.compact(chainsToSearch);

            // Search all chains at once 
            let searchChainPromises = [];
            chainsToSearch.map(chain=>{
              searchChainPromises.push(new Promise(async (resolve,reject)=>{
                let chainNodes = await universe.findOnNodeChain({
                  method: 'latestForEach',
                  searches,
                  chainPubKey: chain.data.pubKey,
                  apiAddress: chain.data.apiAddress
                });
                resolve({
                  nodeChainNode: chain,
                  chainNodes
                });
              }));
            });

            let chainResults = await Promise.all(searchChainPromises);

            console.log('ChainResults', chainResults);

            // console.log('chainNodes', chainNodes);


            // Iterate over CodeNodes and add the latest chainNode that matches 
            // - update status 
            CodeNodes.forEach(node=>{
              if(!node.data.key){
                // already skipped
                console.log('skipped');
                return;
              }
              // find matches from chain 
              // - currently: just shows __status for matching chain 
              // - TODO: check all chains 
              let recent;
              chainResults.forEach(chainResult=>{
                if(!node.data.nodeChainNode){
                  // missing nodeChainNode 
                  return false;
                }
                if(chainResult.nodeChainNode.data.username == node.data.nodeChainNode.data.username &&
                   chainResult.nodeChainNode.data.network == node.data.nodeChainNode.data.network
                ){
                  chainResult.chainNodes.forEach(cn=>{

                    if(node.data.author && 
                      node.data.key == cn.ref && 
                      node.data.author == cn.author
                      ){
                      recent = cn;
                      return true;
                    } else if(
                      node.data.key == cn.ref) {
                      recent = cn;
                      return true;
                    }
                    return false;
                  })
                }
              });
              if(!recent){
                console.log('not_found_on_chain');
                node.__status = 'not_found_on_chain';
                return;
              }

              // TODO: have a __status for a new author? 

              if(node.data.version && node.data.version.toString() == recent.version.toString()){
                node.__status = 'same_version';
              } else {
                // new version available!
                node.__status = 'diff_version';
                node.ChainNode = recent;
              }

            })

            // let componentNode = await universe.runInVM(result[0],{});
            this.setState({
              nodes: CodeNodes,
              fetching: false
            })

            return resolve(CodeNodes);


          });

        }

        @autobind
        handleClickNode(node){
          console.log('Node',node);
        }

        @autobind
        async updateNewCode(){

          this.setState({
            fetching: true
          })

          let CodeNodes = this.state.nodes;

          // update local nodes 
          // - data.[note, code, version]
          try {
            for(let node of CodeNodes){
              if(node.__status == 'diff_version'){
                node.data.code = node.ChainNode.data.code;
                node.data.note = node.ChainNode.data.note;
                node.data.version = node.ChainNode.data.version;

                console.log('Updating Node:', this.state.status, node);

                let updatedNode;

                // Update according to status 
                switch(this.state.status){
                  case 'local':
                    updatedNode = await universe.updateNode(node); 
                    break;
                  case 'remote':
                    // console.log('UPDATE REMOTE:', node);
                    updatedNode = await this.makeSaveRequest(node);
                    break;
                  case 'thru':
                    // TODO
                  default:
                    break;

                }

              }
            }
          }catch(err){
            console.error(err);
          }


          // Get latest for local nodes from NodeChain
          switch(this.state.status){
            case 'local':
              WINDOW.alert('Updated. Might need to reload page for visual changes to take effect');
              break;
            case 'remote':
              WINDOW.alert('Remote Second Updated');
              break;
            case 'thru':
              // TODO
            default:
              break;

          }

          this.fetchAll();

          // setTimeout(()=>{
          //   WINDOW.location.reload();
          // },1000);

        }

        @autobind
        makeSaveRequest(tmpNodeToSave){
          return new Promise(async (resolve, reject) => {

            let nodeToSave = {
              _id: tmpNodeToSave._id,
              data: tmpNodeToSave.data
            }

            // Make request to save Node on remote 
            try {

              let ExternalIdentityNodes = await universe.searchMemory({
                filter: {
                  sqlFilter: {
                    type: 'external_identity:0.0.1:local:8982f982j92',
                    data: {
                      commonName: 'main'
                    }
                  }
                }
              });
              let ExternalIdentityNode = ExternalIdentityNodes[0];

              let response = await universe.loadAndRunCapability('TalkToSecond',{},{
                type: 'standard_capability_action:0.0.1:local:298j291bs',
                data: {
                  action: 'send',
                  options: {
                    ExternalIdentityNode,
                    RequestNode: {
                      type: 'run_action_sequence:0.0.1:local:293fh8239hsdf23f',
                      data: {
                        actions: [
                          {
                            matchActionType: 'update_node:0.0.1:local:392821982y3',
                            dataForAction: {
                              type: 'node:0.0.1:local:123445',
                              data: nodeToSave
                            }
                          }
                        ]

                      }
                    }
                  }
                }
              });

              console.log('Update response:', response);

              return resolve({
                type: 'node:...',
                data: response.data.actionResponses[0]
              });

            } catch(err){
              console.error('Failed updating remote code node');

              WINDOW.alert('Failed');

              return reject();

            }

          })
        }

        @autobind
        renderStatus(node){

          let opacity = 1;
          let color = '';
          let icon = '';

          switch(node.__status){
            case 'missing_local_ref':
              opacity = 0.5;
              color = 'has-text-danger';
              icon = 'fa-exclamation-triangle';
              break;

            case 'searching':
              icon = 'fa-spinner';
              break;

            case 'not_found_on_chain':
              color = 'has-text-warning';
              icon = 'fa-exclamation-triangle';
              break;

            case 'same_version':
              icon = 'fa-angle-double-right';
              break;

            case 'diff_version':
              color = 'has-text-success'
              icon = 'fa-plus-square';
              break;

            default:
              icon = '';
              break;
          }

          return (

            <div className="hover-item tooltip-holder">
              <div className="tooltip-trigger">
                <span className={"icon " + color} style={{opacity}}>
                  <i className={"fa " + icon}></i>
                </span>
              </div>
              <div className="tooltip">
                {node.__status}
              </div>
            </div>

          )
        }

        @autobind
        renderNodes(nodes, parentInFilter){

          let filter = this.state.text;

          nodes = universe.lodash.sortBy(nodes, 'updatedAt');
          // console.log('Nodes',nodes);

          return (
            <div className="">
              {
                nodes.map(node=>{

                  // include this node if it, or of of it's children, matches
                  // - highlight if a match 

                  const nodeMatchFunc = (node) => {
                    let nodeMatch;
                    if(node.type && node.type.includes(filter)){
                      nodeMatch = true;
                    }
                    if(node.data && JSON.stringify(node.data).includes(filter)){
                      nodeMatch = true;
                    }
                    // type (t:)
                    if(filter.includes('t:') && node.type){
                      let tmpFilter = filter.split('t:')[1];
                      if(node.type && node.type.includes(tmpFilter)){
                        nodeMatch = true;
                      }
                    }
                    // data (d:) 
                    if(filter.includes('d:')){
                      let tmpFilter = filter.split('d:')[1];
                      if(node.data && JSON.stringify(node.data).includes(tmpFilter)){
                        nodeMatch = true;
                      }
                    }

                    return nodeMatch;

                  }

                  const childMatchFunc = (nodes) => {
                    for(let tmpNode of nodes){
                      if(nodeMatchFunc(tmpNode)){
                        return true;
                      }
                      if(childMatchFunc(tmpNode.nodes)){
                        return true;
                      }
                    }
                    return false;
                  }

                  let hasFilter = filter.length ? true:false;
                  let nodeMatch;
                  let childMatch;
                  if(hasFilter){

                    nodeMatch = nodeMatchFunc(node);
                    childMatch = childMatchFunc(node.nodes || []);

                    // check children 
                    // - lots of recursion here, probably should memoize 


                    // // filter by "text" if set 
                    // nodes = nodes.filter(node=>{
                    //   return true;
                    // })
                  }

                  if(hasFilter && !nodeMatch && !childMatch && !parentInFilter){
                    return undefined; // skip
                  }

                  let tooltip = null;
                  // return a different tooltip depending on the type
                  let nodeTypeFirst = node.type ? node.type.split(':')[0]:null;
                  try {
                    switch(nodeTypeFirst){
                      case 'react_component':
                        tooltip = node.data.internalId;
                        break;

                      case 'parent_of_nodes_to_learn':
                        tooltip = node.data.learn;
                        break;

                      case 'text_tag':
                        tooltip = node.data.text;
                        break;

                      case 'capability':
                        tooltip = node.data.key;
                        break;

                      case 'code':
                        tooltip = node.data.note;
                        break;

                      case 'external_identity':
                        tooltip = node.data.commonName || '(no commonName)';
                        break;

                      case 'external_identity_connect_method':
                        tooltip = node.data.connection;
                        break;

                      case 'folder':
                        tooltip = node.data.key;
                        break;

                      case 'node_repo_on_github':
                        tooltip = node.data.repo;
                        break;

                      default:
                        break;
                    }
                  }catch(err){
                  }

                  return (
                    <table className="" key={node._id}>
                      <tbody>
                        <tr>
                          <td style={{width:'28px'}}>
                            {this.renderStatus(node)}
                          </td>
                          <td>
                            <div onClick={e=>this.handleClickNode(node)} style={{cursor:'pointer', color: nodeMatch ? 'blue':'inherit'}}>

                                <div className="hover-item tooltip-holder">
                                  <div className="tooltip-trigger">
                                    {
                                      (this.props.state.editingNode && this.props.state.editingNode._id == node._id) ?
                                        <strong>
                                          {node.type}
                                        </strong>
                                        :
                                        node.type
                                    }
                                  </div>
                                  {
                                    !tooltip ? '':
                                    <div className="tooltip">
                                      {tooltip}
                                    </div>
                                  }
                                </div>

                            </div>
                          </td>
                        </tr>
                      </tbody>
                    </table>
                  )
                })
              }
            </div>
          )
        }

        render(){

          let nodes = this.state.nodes;

          // load actions from remote!
          // - want to have some default actions that are easy to call/trigger 
          // - simple way of calling a Capability? 
          let actions = [];

          return (
            <div className="">

              {
                !this.state.pastable ? '':
                <div>
                  <textarea className="textarea" value={this.state.pasteText} onChange={e=>this.setState({pasteText:e.target.value})} />
                  <button className="button is-primary" onClick={this.handleInsertNodePaste}>Save</button>
                  <button className="button pull-right" onClick={e=>this.setState({pastable:false})}>cancel</button>
                </div>
              }

              <strong>

                {
                  this.state.status != 'local' ?'':
                  'Local Code Nodes'
                }
                {
                  this.state.status != 'remote' ? '':
                  'Remote Code Nodes'
                }
                {
                  this.state.status != 'thru' ? '':
                  'Remote Connected Code Nodes'
                }

                &nbsp;

                <button className={"button is-small " + (this.state.fetching ? 'is-loading':'')} onClick={this.fetchAll}>
                  <span className="icon is-small">
                    <i className="fa fa-refresh"></i>
                  </span>
                </button>

                <div className="dropdown is-hoverable is-right pull-right">
                  <div className="dropdown-trigger">
                    <button className="button is-small">
                      <span className="icon is-small">
                        <i className="fa fa-angle-down"></i>
                      </span>
                    </button>
                  </div>
                  <div className="dropdown-menu">
                    <div className="dropdown-content">

                      <a className="dropdown-item" onClick={e=>this.handleChangeStatus('local')}>
                        {
                          this.state.status != 'local' ? 
                          <span className="icon">
                            <i className="fa fa-square"></i>
                          </span>
                          :
                          <span className="icon">
                            <i className="fa fa-check-square"></i>
                          </span>
                        } Local Code Nodes
                      </a>

                      <a className="dropdown-item" onClick={e=>this.handleChangeStatus('remote')}>
                        {
                          this.state.status != 'remote' ? 
                          <span className="icon">
                            <i className="fa fa-square"></i>
                          </span>
                          :
                          <span className="icon">
                            <i className="fa fa-check-square"></i>
                          </span>
                        } Remote Code Nodes
                      </a>

                      <div className="dropdown-divider"></div>

                      <a className="dropdown-item" onClick={this.updateNewCode}>
                        Update all [+] (new version) 
                      </a>

                    </div>
                  </div>
                </div>

              </strong>


              <div>
                <input className="input" value={this.state.text} onChange={e=>this.setState({text:e.target.value})} placeholder="Filter on type/data" />
              </div>

              {this.renderNodes(nodes)}

            </div>
          )
        }
      }

      ListComponent = universe.ReactGlobalState(ListComponent);

      resolve({
        type: 'react_component:0.0.1:local:98912hd89',
        data: ListComponent
      });

    }catch(err){
      resolve({
        ERROR: true, 
        err: err.toString()
      });
    }

  })


