(()=>{

  // This code handles an incoming "run_action_sequence..."

  // expcting a Node that specifies an array of actions to run! 
  // - this is useful when you want to include Authentication 
  //   - every command should include Auth+More 


  return new Promise(async (resolve,reject)=>{
    try {

      // Display the "SetupComponent" or the "MainComponent" depending on if launched/ready 
      console.log('Display Setup or MainComponent?');

      // Find internal identity (if not exists, show setup) 
      try {
        let newIdentityNode = await universe.loadAndRunCapability('Identity',{},{
          type: 'standard_capability_action:0.0.1:local:298j291bs',
          data: {
            action: 'getPrivate',
            options: {}
          }
        });
        console.log('Got newIdentityNode', newIdentityNode);
        if(newIdentityNode.data.error){
          throw "newIdentityNode Error"
        }

      }catch(err){
        // should use setup component 
        console.log('NOT SETUP YET!');

        let result2 = await universe.searchMemory({
          filter: {
            sqlFilter: {
              type: 'react_component:0.0.1:local:98912hd89',
              data: {
                internalId: 'SetupComponent'
              }
            }
          }
        });
        let componentNode2 = await universe.runNodeCodeInVM({
          codeNode: result2[0],
          dataNode: {}
        });
        // let componentNode = await universe.runInVM(result[0],{});

        return resolve(componentNode2);

      }

      let result = await universe.searchMemory({
        filter: {
          sqlFilter: {
            type: 'react_component:0.0.1:local:98912hd89',
            data: {
              internalId: 'MainComponent'
            }
          }
        }
      });
      let componentNode = await universe.runNodeCodeInVM({
        codeNode: result[0],
        dataNode: {}
      });
      // let componentNode = await universe.runInVM(result[0],{});

      return resolve(componentNode);

    }catch(err){
      resolve({ERROR: true, err: err.toString()});
    }

  })


})()
