(()=>{
  return new Promise(async (resolve, reject)=>{
    try {

      console.log('Starting ActAs Capability');

      // Expecting INPUT = capability_input_node:0.0.1:local:29f8239a13h9
      // - capabilityNode: Object,
      // - externalInputNode: Object,

      // Load the "Identify with Remote Second" capability

      // let newIdentityNode = await universe.loadAndRunCapability('IdentityInitiate',{},{
      //   type: 'standard_capability_action:0.0.1:local:298j291bs',
      //   data: {
      //     action: 'start',
      //     options: {
      //       externalIdentityPublicKey: ExternalIdentityNode,
      //       internalUserPassphrase
      //     }
      //   }
      // });

      let cmdInputNode = INPUT.data.externalInputNode;

      if(cmdInputNode.type != 'standard_capability_action:0.0.1:local:298j291bs'){
        console.log('Unexpected input schema');
        return reject();
      }

      let inputAction = cmdInputNode.data.action;
      let inputOpts = cmdInputNode.data.options;

      let { externalIdentityPublicKey, internalUserPassphrase } = inputOpts;

      switch(inputAction){
        case 'start':
          break;

        default:
          return reject();
      }


      // Currently ExternalIdentity is unique by the publicKey (cant have multiple identities with the same publicKey) 
      // - can have multiple connect_method's though! (websocket, etc.) 


      // Upgrades token (gets a new token for ExternalIdentityNode, updates local memory) 
      //- adds InternalUser for remote 


      // Get local ExternalIdentity 
      let nodes = await universe.searchMemory({
        filter: {
          sqlFilter: {
            type: 'external_identity:0.0.1:local:8982f982j92',
            data: {
              publicKey: externalIdentityPublicKey
            }
          },
          // function for returning data from the Node, after filtering a bit 
          // - includes both the Node, and Nodes with nodeId (pointers) 
          filterNodes: tmpNodes=>{
            return tmpNodes;
          }
        }
      });

      if(!nodes.length){
        return resolve({
          type: 'internal_error_output:0.0.1:local:32948x2u3cno2c',
          data: {
            str: 'Missing existing ExternalIdentity node',
            publicKey: INPUT.data
          }
        })
      }

      let ExternalIdentityNode = nodes[0];


      // // has connect_method (external_identity_connect_method:0.0.1:local:382989239hsdfmn)?
      // let connectNode = universe.lodash.find(ExternalIdentityNode.nodes, {type: 'external_identity_connect_method:0.0.1:local:382989239hsdfmn'});
      // if(!connectNode){
      //   return resolve({
      //     type: 'internal_error_output:0.0.1:local:32948x2u3cno2c',
      //     data: {
      //       str: 'Missing existing ExternalIdentity connect_method child!'
      //     }
      //   })
      // }



      // TalkToSecond (runs sequence of auth w/ token, then upgrade) 
      console.log('Token FOR ActAs:', ExternalIdentityNode.data.token, ExternalIdentityNode);
      let response = await universe.loadAndRunCapability('TalkToSecond',{},{
        type: 'standard_capability_action:0.0.1:local:298j291bs',
        data: {
          action: 'send',
          options: {
            ExternalIdentityNode,
            RequestNode: {
              type: 'run_action_sequence:0.0.1:local:293fh8239hsdf23f',
              data: {
                actions: [

                  {
                    matchActionType: 'identify_via_token:0.0.1:local:237823783g2123',
                    dataForAction: {
                      type: 'string:...',
                      data: ExternalIdentityNode.data.token
                    }
                  },

                  {
                    matchActionType: 'identify_token_upgrade_to_internal_user:0.0.1:local:9281198u',
                    dataForAction: {
                      type: 'user_passphrase:0.01...',
                      data: {
                        passphrase: internalUserPassphrase
                      }
                    }
                  }
                ]

              }
            }
          }
        }
      });

      console.log('ActAs Response:', response);

      // expecting type: upgrade_token_response:0.0.1:local:289fh2389h23sfsvds

      let tokenResponse = response.data.actionResponses[1];
      if(tokenResponse.type != 'upgrade_token_response:0.0.1:local:289fh2389h23sfsvds'){
        console.error('Failed Identity Upgrade to InternalUser');
        return reject({
          msg: 'Failed Identity Upgrade to InternalUser', 
          error: true
        })
      }
      let newToken = tokenResponse.data.token;

      console.log('newToken for ActsAs:', newToken);

      // Update/save local ExternalIdentityNode with new token 
      ExternalIdentityNode.data.token = newToken;
      let updatedExternalIdentityNode = await universe.updateNode(ExternalIdentityNode);

      console.log('Updated local ExternalIdentityNode', updatedExternalIdentityNode);

      return resolve({
        type: 'identity_upgrade_finish:0.0.1:local:981h3001j3',
        data: {
          updatedExternalIdentityNode,
          newToken
        }
      });


    }catch(err){
      resolve({ERROR: true, err: err.toString()});
    }


  })
})()
